"use strict";document.addEventListener("DOMContentLoaded",function(){var gallery=document.querySelector(".gallery");gallery.addEventListener("click",openImage,false);function openImage(event){var imgSrc=event.target.src;var imgTitle=event.target.alt;var overlayString="overlay";var overlay=document.querySelector(".".concat(overlayString));var overlayImg=document.querySelector(".".concat(overlayString,"__img"));var overlayTitle=document.querySelector(".".concat(overlayString,"__title"));overlayImg.src=imgSrc;overlayTitle.innerHTML=imgTitle;overlay.classList.add("openOverlay");var closeBtn=document.querySelector(".close");closeBtn.addEventListener("click",function(){overlay.classList.remove("openOverlay")})}});